import{_ as s,k as a,F as n,W as e}from"./chunks/framework.e8799e72.js";const l=JSON.parse('{"title":"Styling","description":"","frontmatter":{"head":[["meta",{"name":"og:title","content":"Styling"}],["meta",{"name":"og:description","content":"Shopware Frontends Demo Store Template applies a utility-first styling approach based on unocss. You can either follow this approach or use custom styling."}],["meta",{"name":"og:image","content":"https://frontends-og-image.vercel.app/Styling.png"}]],"nav":{"position":30}},"headers":[{"level":2,"title":"Utility CSS","slug":"utility-css","link":"#utility-css","children":[{"level":3,"title":"Reusability","slug":"reusability","link":"#reusability","children":[]},{"level":3,"title":"Responsive Design","slug":"responsive-design","link":"#responsive-design","children":[]},{"level":3,"title":"State Variants","slug":"state-variants","link":"#state-variants","children":[]}]},{"level":2,"title":"Use a custom CSS Framework","slug":"use-a-custom-css-framework","link":"#use-a-custom-css-framework","children":[{"level":3,"title":"Remove unocss from the Demo Store Template","slug":"remove-unocss-from-the-demo-store-template","link":"#remove-unocss-from-the-demo-store-template","children":[]}]}],"relativePath":"framework/styling.md","filePath":"framework/styling.md"}'),o={name:"framework/styling.md"},t=[e('<h1 id="styling" tabindex="-1">Styling <a class="header-anchor" href="#styling" aria-label="Permalink to &quot;Styling&quot;">​</a></h1><p>Shopware Frontends <a href="./../getting-started/templates.html">Demo Store Template</a> applies a utility-first styling approach based on <a href="https://github.com/unocss/unocss" target="_blank" rel="noreferrer">unocss</a>. You can either follow this approach or use <a href="#use-a-custom-css-framework">custom styling</a>.</p><h2 id="utility-css" tabindex="-1">Utility CSS <a class="header-anchor" href="#utility-css" aria-label="Permalink to &quot;Utility CSS&quot;">​</a></h2><p>Unocss supports multiple CSS frameworks, including</p><ul><li>Tailwind CSS</li><li>Windy CSS</li><li>Bootstrap</li></ul><p>This means you can use utilities like <code>mt-10</code> or <code>bg-gray-100</code> in all of your components along with styles like <code>col-md-3</code>. Note, that the <a href="./../getting-started/templates.html">Demo Store Template</a> applies only Tailwind CSS syntax and does not mix any of the approaches.</p><p>Unocss will analyse your components and generate a CSS file that contains only the utility classes used in the implementation.</p><p>As an introduction, we recommend reading the <a href="https://tailwindcss.com/docs/utility-first" target="_blank" rel="noreferrer">Utility-First Fundamentals</a> article from Tailwind CSS.</p><p>When building layouts in a utility-first manner, you should follow some fundamental rules.</p><h3 id="reusability" tabindex="-1">Reusability <a class="header-anchor" href="#reusability" aria-label="Permalink to &quot;Reusability&quot;">​</a></h3><p>There will be cases when you would like to create a class instead of using a long list of utility classes for multiple components. In that case, consider creating a reusable component instead:</p><div class="flex justify-center"><img class="object-cover w-12 h-12 rounded-full border-3 border-white dark:border-#1a1a1a mr--6" src="https://picsum.photos/id/18/100/100"><img class="object-cover w-12 h-12 rounded-full border-3 border-white dark:border-#1a1a1a mr--6" src="https://picsum.photos/id/12/200/200"><img class="object-cover w-12 h-12 rounded-full border-3 border-white dark:border-#1a1a1a mr--6" src="https://picsum.photos/id/29/200/200"></div><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span></span>\n<span class="line"><span style="color:#89DDFF;">  </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">object-cover w-12 h-12 rounded-full border-3 border-white mr--6</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">  </span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">https://picsum.photos/id/18/100/100</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">/&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span></span>\n<span class="line"><span style="color:#89DDFF;">  </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">object-cover w-12 h-12 rounded-full border-3 border-white mr--6</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">  </span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">https://picsum.photos/id/12/200/200</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">/&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span></span>\n<span class="line"><span style="color:#89DDFF;">  </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">object-cover w-12 h-12 rounded-full border-3 border-white mr--6</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">  </span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">https://picsum.photos/id/29/200/200</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">/&gt;</span></span>\n<span class="line"></span></code></pre></div><p>will become</p><div class="language-vue"><button title="Copy Code" class="copy"></button><span class="lang">vue</span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;">&lt;!-- ImageCircle.vue --&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">script</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">setup</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"><span style="color:#82AAFF;">defineProps</span><span style="color:#A6ACCD;">([</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">imageSrc</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">])</span><span style="color:#89DDFF;">;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span></span>\n<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">object-cover w-12 h-12 rounded-full border-3 border-white mr--6</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#C792EA;">:src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">imageSrc</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">  /&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span></code></pre></div><div class="language-vue"><button title="Copy Code" class="copy"></button><span class="lang">vue</span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;">&lt;!--- ImageContainer.vue --&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">script</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">setup</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"><span style="color:#82AAFF;">defineProps</span><span style="color:#A6ACCD;">([</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">images</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">])</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">ImageCircle</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">v-for</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">image in images</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">:imageSrc</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">image</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span></code></pre></div><h3 id="responsive-design" tabindex="-1">Responsive Design <a class="header-anchor" href="#responsive-design" aria-label="Permalink to &quot;Responsive Design&quot;">​</a></h3><p>Start your layout from the smallest viewport and work your way up. There are built in prefixes for the viewport sizes:</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">div</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">grid md:grid-cols-2</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#676E95;font-style:italic;">&lt;!-- some html --&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">div</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span></code></pre></div><h3 id="state-variants" tabindex="-1">State Variants <a class="header-anchor" href="#state-variants" aria-label="Permalink to &quot;State Variants&quot;">​</a></h3><p>Similar to viewport breakpoints, you can also use state variants with prefixes:</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">div</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">group flex justify-center</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">input</span></span>\n<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#C792EA;">class</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">hover:shadow-xl border-2 border-indigo rounded-md p-3 shadow-md</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#C792EA;">type</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">text</span><span style="color:#89DDFF;">&quot;</span></span>\n<span class="line"><span style="color:#89DDFF;">  /&gt;</span></span>\n<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">div</span><span style="color:#89DDFF;">&gt;</span></span>\n<span class="line"></span></code></pre></div><div class="flex justify-center"><input class="hover:shadow-xl focus:border-red border-indigo border-2 rounded-md p-3 shadow-md" type="text" placeholder="Hover me 🙂"></div><h2 id="use-a-custom-css-framework" tabindex="-1">Use a custom CSS Framework <a class="header-anchor" href="#use-a-custom-css-framework" aria-label="Permalink to &quot;Use a custom CSS Framework&quot;">​</a></h2><p>If you want to use a different CSS framework or fully custom styling, it&#39;s recommended to use the <a href="./../getting-started/templates/blank-template.html">Blank Template</a> as a starting point. It has no pre-installed CSS framework and you can install you own.</p><h3 id="remove-unocss-from-the-demo-store-template" tabindex="-1">Remove unocss from the Demo Store Template <a class="header-anchor" href="#remove-unocss-from-the-demo-store-template" aria-label="Permalink to &quot;Remove unocss from the Demo Store Template&quot;">​</a></h3><p>However, it&#39;s also possible to remove unocss from the <a href="./../getting-started/templates/demo-store-template.html">Demo Store Template</a>. This might be applicable when you want to make use of the component structure and logic that&#39;s already provided by the template.</p><p>Remove the <code>unocss</code> dependency from the <code>package.json</code> file</p><div class="language-diff"><button title="Copy Code" class="copy"></button><span class="lang">diff</span><pre><code><span class="line"><span style="color:#A6ACCD;">/* package.json */</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">    &quot;@unocss/nuxt&quot;: &quot;^0.45.13&quot;,</span></span>\n<span class="line"></span></code></pre></div><p>Remove the unocss imports, build modules and configuration from the <code>nuxt.config.js</code> file</p><div class="language-diff"><button title="Copy Code" class="copy"></button><span class="lang">diff</span><pre><code><span class="line"><span style="color:#A6ACCD;">/* nuxt.config.js */</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#A6ACCD;"> import { defineNuxtConfig } from &quot;nuxt&quot;;</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">import transformerDirective from &quot;@unocss/transformer-directives&quot;;</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">import presetIcons from &quot;@unocss/preset-icons&quot;;</span></span>\n<span class="line"></span>\n<span class="line"><span style="color:#A6ACCD;"> export default defineNuxtConfig({</span></span>\n<span class="line"><span style="color:#A6ACCD;">   },</span></span>\n<span class="line"><span style="color:#A6ACCD;">   buildModules: [</span></span>\n<span class="line"><span style="color:#A6ACCD;">     &quot;@vueuse/nuxt&quot;,</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">    &quot;@unocss/nuxt&quot;,</span></span>\n<span class="line"><span style="color:#A6ACCD;">     &quot;@shopware-pwa/nuxt3-module&quot;,</span></span>\n<span class="line"><span style="color:#A6ACCD;">     &quot;@shopware-pwa/cms-base&quot;,</span></span>\n<span class="line"><span style="color:#A6ACCD;">   ],</span></span>\n<span class="line"><span style="color:#A6ACCD;">   vueuse: {</span></span>\n<span class="line"><span style="color:#A6ACCD;">     ssrHandlers: true,</span></span>\n<span class="line"><span style="color:#A6ACCD;">   },</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">  unocss: {</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">    uno: true, // enabled `@unocss/preset-uno`</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">    icons: true, // enabled `@unocss/preset-icons`</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">    attributify: true, // enabled `@unocss/preset-attributify`,</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">    ...</span></span>\n<span class="line"><span style="color:#89DDFF;">-</span><span style="color:#F07178;">  },</span></span>\n<span class="line"></span></code></pre></div><p>Eventually, run <code>pnpm install</code> to remove the unocss dependency from your installation.</p>',32)];const p=s(o,[["render",function(s,e,l,o,p,r){return a(),n("div",null,t)}]]);export{l as __pageData,p as default};
